from django.conf.urls import urlfrom . import viewsurlpatterns = [    # 获取QQ登录界面url    url(r'^qq/authorization/$', views.QQAuthURLView.as_view()),    # QQ登录后的回调处理    url(r'^oauth_callback/$', views.QQAuthView.as_view()),    # sina微博登录界面url    url(r'^sina/authorization/$', views.SinaAuthURLView.as_view()),    # sina微博登录后的回调处理    url(r'^sina_callback/$', views.SinaCallbackView.as_view()),    url(r'^oauth/sina/user/$', views.SinaCallback2View.as_view())]